            L.DomUtil.addClass(this._container, 'debugWindowControl_active');
            this.loggerCollection.map(function(msgModel) {
                var el = L.DomUtil.create('div', '', this._container);
                el.innerHTML = msgModel.get('message');
            }.bind(this));
